const items = $input.all();
const processedItems = [];
const sourceData = items[0].json;

// Helper function to safely get nested properties
function getNestedValue(obj, path, defaultValue = "") {
  try {
    const parts = path.split('.');
    let current = obj;

    for (const part of parts) {
      if (part.includes('[') && part.includes(']')) {
        // Handle array access
        const arrayPart = part.split('[')[0];
        const index = parseInt(part.split('[')[1].split(']')[0]);

        if (!current[arrayPart] || !Array.isArray(current[arrayPart]) || !current[arrayPart][index]) {
          return defaultValue;
        }

        current = current[arrayPart][index];
      } else {
        // Handle object property access
        if (current === null || current === undefined || !current[part]) {
          return defaultValue;
        }

        current = current[part];
      }
    }

    return current || defaultValue;
  } catch (error) {
    return defaultValue;
  }
}

// Function to find tweet data
function findAllTweetData(obj) {
  if (!obj || typeof obj !== 'object') {
    return;
  }

  // Check for tweet structure
  if (obj.content && obj.content.itemContent && obj.content.itemContent.tweet_results) {
    const tweetResult = getNestedValue(obj, 'content.itemContent.tweet_results.result');

    if (tweetResult) {
      const userLegacy = getNestedValue(tweetResult, 'core.user_results.result.legacy');
      const tweetLegacy = getNestedValue(tweetResult, 'legacy');

      if (userLegacy && tweetLegacy) {
        // Extract the data with renamed fields
        processedItems.push({
          json: {
            username: getNestedValue(userLegacy, 'screen_name'),
            followers: getNestedValue(userLegacy, 'followers_count', 0),
            media_url_https: getNestedValue(tweetLegacy, 'entities.media[0].media_url_https'),
            "conversation ID": getNestedValue(tweetLegacy, 'id_str'),
            "Tweet text": getNestedValue(tweetLegacy, 'full_text'),
            retweet_count: getNestedValue(tweetLegacy, 'retweet_count', 0),
            reply_count: getNestedValue(tweetLegacy, 'reply_count', 0),
            quote_count: getNestedValue(tweetLegacy, 'quote_count', 0),
            created_at: getNestedValue(tweetLegacy, 'created_at')
          }
        });
      }
    }
  }

  // Process arrays
  if (Array.isArray(obj)) {
    for (let i = 0; i < obj.length; i++) {
      findAllTweetData(obj[i]);
    }
  } else {
    // Process object properties
    for (const key in obj) {
      findAllTweetData(obj[key]);
    }
  }
}

try {
  findAllTweetData(sourceData);
  console.log(`Found ${processedItems.length} tweet records`);
} catch (error) {
  console.log("Error:", error.message);
}

// Return all processed items
return processedItems;

const items = $input.all();
const processedItems = [];
const sourceData = items[0].json;
const processedTweetIds = new Set(); // For deduplication

// Helper function to safely get nested properties
function getNestedValue(obj, path, defaultValue = "") {
  try {
    const parts = path.split('.');
    let current = obj;

    for (const part of parts) {
      if (current === null || current === undefined || !current[part]) {
        return defaultValue;
      }
      current = current[part];
    }

    return current || defaultValue;
  } catch (error) {
    return defaultValue;
  }
}

// Helper function to process tweet data
function processTweetData(tweetResult) {
  if (!tweetResult || !tweetResult.legacy) return null;

  const tweetId = getNestedValue(tweetResult, 'legacy.id_str');
  const tweetText = getNestedValue(tweetResult, 'legacy.full_text');

  // Skip if already processed or contains a link
  if (processedTweetIds.has(tweetId) || tweetText.includes('https')) {
    return null;
  }

  processedTweetIds.add(tweetId);

  return {
    json: {
      "Tweet text": tweetText,
      views_count: getNestedValue(tweetResult, 'views.count', 0),
      retweet_count: getNestedValue(tweetResult, 'legacy.retweet_count', 0),
      reply_count: getNestedValue(tweetResult, 'legacy.reply_count', 0),
      favorite_count: getNestedValue(tweetResult, 'legacy.favorite_count', 0),
      bookmark_count: getNestedValue(tweetResult, 'legacy.bookmark_count', 0),
      created_at: getNestedValue(tweetResult, 'legacy.created_at')
    }
  };
}

try {
  // Get all instructions
  const instructions = sourceData.user.result.timeline_v2.timeline.instructions;

  // Process each instruction
  instructions.forEach(instruction => {
    if (instruction.entries && Array.isArray(instruction.entries)) {
      instruction.entries.forEach(entry => {
        // Case 1: Direct tweet data under itemContent
        if (entry.content && entry.content.itemContent && entry.content.itemContent.tweet_results) {
          const tweetResult = entry.content.itemContent.tweet_results.result;
          const tweetData = processTweetData(tweetResult);
          if (tweetData) processedItems.push(tweetData);
        }

        // Case 2: Tweet data under items array (replies)
        if (entry.content && entry.content.items && Array.isArray(entry.content.items)) {
          entry.content.items.forEach(item => {
            if (item.item && item.item.itemContent && item.item.itemContent.tweet_results) {
              const tweetResult = item.item.itemContent.tweet_results.result;
              const tweetData = processTweetData(tweetResult);
              if (tweetData) processedItems.push(tweetData);
            }
          });
        }
      });
    }
  });

  console.log(`Successfully extracted ${processedItems.length} unique tweets/replies without links`);
} catch (error) {
  console.log("Error:", error.message);
}

// Return all processed items
return processedItems;

